import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaMicrosoft } from 'react-icons/fa';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/auth/user', { withCredentials: true })
      .then(res => setUser(res.data))
      .catch(() => setUser(null));
  }, []);

  const handleLogin = () => {
    window.location.href = 'http://localhost:5000/auth/login';
  };

  const handleLogout = () => {
    window.location.href = 'http://localhost:5000/auth/logout';
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-100 to-blue-200">
      <div className="bg-white p-10 rounded-xl shadow-xl w-full max-w-lg text-center">
        <h1 className="text-3xl font-bold text-gray-800 mb-4">Azure AD SSO Login</h1>

        {!user ? (
          <>
            <p className="text-gray-600 mb-6">Sign in using your Microsoft Account</p>
            <button
              onClick={handleLogin}
              className="w-full flex items-center justify-center gap-3 py-3 px-5 bg-blue-600 hover:bg-blue-700 text-white text-lg rounded-lg transition-all"
            >
              <FaMicrosoft size={20} />
              Login with Microsoft
            </button>
          </>
        ) : (
          <div className="text-center">
            <p className="text-lg text-gray-700 mb-4">Welcome, <span className="font-semibold">{user.displayName}</span></p>
            <button
              onClick={handleLogout}
              className="mt-4 px-5 py-3 bg-red-500 hover:bg-red-600 text-white rounded-lg transition-all"
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
