import React, { useEffect, useState } from 'react';
import { Camera } from 'lucide-react';

// We can't use axios directly in this environment, so we'll simulate the API calls
function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setLoading(false);
      // User is initially null as in your original code
    }, 800);
  }, []);

  const handleLogin = () => {
    // In real app: window.location.href = 'http://localhost:5000/auth/login';
    console.log('Redirecting to Microsoft login...');
    // For demo, simulate successful login
    setLoading(true);
    setTimeout(() => {
      setUser({ displayName: 'John Doe', email: 'john.doe@company.com' });
      setLoading(false);
    }, 800);
  };

  const handleLogout = () => {
    // In real app: window.location.href = 'http://localhost:5000/auth/logout';
    console.log('Logging out...');
    setLoading(true);
    setTimeout(() => {
      setUser(null);
      setLoading(false);
    }, 500);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-6">
      <div className="bg-white rounded-xl shadow-xl overflow-hidden w-full max-w-md">
        <div className="bg-gradient-to-r from-blue-600 to-indigo-700 p-6 text-center">
          <div className="flex justify-center mb-3">
            <div className="bg-white/20 backdrop-blur-sm p-3 rounded-full">
              <Camera size={24} className="text-white" />
            </div>
          </div>
          <h1 className="text-white text-2xl font-bold">Company Portal</h1>
          <p className="text-blue-100 mt-1">Secure Sign-in with Microsoft</p>
        </div>

        <div className="p-8">
          {loading ? (
            <div className="flex flex-col items-center justify-center py-6">
              <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-blue-600"></div>
              <p className="mt-4 text-gray-600">One moment please...</p>
            </div>
          ) : !user ? (
            <div className="space-y-8">
              <div className="text-center">
                <h2 className="text-gray-800 text-xl font-medium">Welcome Back</h2>
                <p className="text-gray-500 mt-2">Sign in with your Microsoft account to continue</p>
              </div>

              <button
                onClick={handleLogin}
                className="w-full flex items-center justify-center gap-3 bg-white border border-gray-300 hover:bg-gray-50 text-gray-800 font-medium py-3 px-4 rounded-lg transition-colors duration-300 shadow-sm"
              >
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 23 23">
                  <path fill="#f3f3f3" d="M0 0h23v23H0z"></path>
                  <path fill="#f35325" d="M1 1h10v10H1z"></path>
                  <path fill="#81bc06" d="M12 1h10v10H12z"></path>
                  <path fill="#05a6f0" d="M1 12h10v10H1z"></path>
                  <path fill="#ffba08" d="M12 12h10v10H12z"></path>
                </svg>
                Login with Microsoft
              </button>

              <div className="text-center">
                <p className="text-xs text-gray-500">
                  By continuing, you agree to our Terms of Service and Privacy Policy
                </p>
              </div>
            </div>
          ) : (
            <div className="text-center space-y-6">
              <div className="inline-flex items-center justify-center h-20 w-20 rounded-full bg-blue-100 text-blue-800 text-xl font-bold">
                {user.displayName.split(' ').map(n => n[0]).join('')}
              </div>

              <div>
                <h2 className="text-gray-800 text-xl font-medium">Welcome, {user.displayName}</h2>
                <p className="text-gray-500 text-sm mt-1">{user.email}</p>
              </div>

              <div className="pt-4">
                <button
                  onClick={handleLogout}
                  className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-6 rounded-lg transition-colors duration-300"
                >
                  Sign Out
                </button>
              </div>
            </div>
          )}
        </div>

        <div className="border-t border-gray-200 bg-gray-50 p-4 text-center">
          <p className="text-gray-600 text-sm">
            © {new Date().getFullYear()} Your Company. All rights reserved.
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
